    "bibtex": "@incollection{3711,\n  address = {Wiesbaden},\n  year = {2018},\n  booktitle = {Sexuelle Gewalt in p\u00e4dagogischen Kontexten},\n  editor = {Wazlawik, Martin and Vo\u00df, Heinz-J\u00fcrgen and Retkowski, Alexandra and Henningsen, Anja and Dekker, Arne},\n  isbn = {9783658180003},\n  publisher = {{Springer VS}},\n  pages = {247--260},\n  keywords = {Geschlecht // gender;Macht // power;Merseburg;P\u00e4dagogische Fachkr\u00e4fte // pedagogial staff;Professionalit\u00e4t // professionality;Sexualit\u00e4t // sexuality;Soziale Arbeit // social work},\n  title = {Interviews zu Sexualit\u00e4t und Geschlecht mit Fachkr\u00e4ften in der Sozialen Arbeit: Die Bedeutung einer reflektieren Haltung f\u00fcr das professionelle Handeln am Beispiel des Umgangs mit Macht und Diskriminierungserfahrungen im Arbeitskontext},\n  author = {Krolzik-Matthei, Katja and Linke, Torsten and Vo\u00df, Heinz-J\u00fcrgen}\n}"
