    "bibtex": "@incollection{4584,\n  address = {Weinheim},\n  year = {2018},\n  booktitle = {Sexuelle Gewalt in Kindheit und Jugend},\n  editor = {Andresen, Sabine and Tippelt, Rudolf},\n  isbn = {978-3-7799-3525-4},\n  publisher = {{Beltz Juventa}},\n  pages = {40--53},\n  keywords = {Diskurs // discourse;Diskursanalyse // discourse analysis;Forschungsergebnisse // research results;Kindeswohl // child\u00b4s welfare;Medien // media;Medienanalyse // media analysis;sexueller Kindesmissbrauch // child sexual abuse;Skandalisierung // scandalisation},\n  title = {Kindeswohl als kollektives Orientierungsmuster?},\n  author = {G\u00f6rgen, Arno and S\u00f6hner, Felicitas and Fangerau, Heiner}\n}"
